mapper.insert:
insert into doctor (
    username, password, real_name, avatar, dept_id,
    title, intro, status, max_daily_audit, work_status,
    create_time, update_time, create_user, update_user
) values (
             #{username}, #{password}, #{realName}, #{avatar}, #{deptId},
             #{title}, #{intro}, #{status}, #{maxDailyAudit}, #{workStatus},
             #{createTime}, #{updateTime}, #{createUser}, #{updateUser}
         )

mapper.update:
update doctor
real_name = #{realName}, avatar = #{avatar}, dept_id = #{deptId}, title = #{title}, intro = #{intro}, status = #{status}, max_daily_audit = #{maxDailyAudit}, update_time = #{updateTime}, update_user = #{updateUser},
where id = #{id}

mapper.select:
select
doc.*,
dep.name as deptName  from doctor doc
left outer join department dep on doc.dept_id = dep.id


        and doc.real_name like concat('%', #{realName}, '%')



        and doc.dept_id = #{deptId}



        and doc.status = #{status}



        and doc.work_status = #{workStatus}


    and doc.status = 1

order by doc.create_time desc

select
doc.*,
dep.name as deptName  from doctor doc
left outer join department dep on doc.dept_id = dep.id


        and doc.real_name like concat('%', #{realName}, '%')



        and doc.dept_id = #{deptId}



        and doc.status = #{status}



        and doc.work_status = #{workStatus}


order by doc.create_time desc
